import axios from "axios"

export default async function downloadProof(data) {
	try {
		let token = null
		const tokenRes = await axios.post("https://sp-api-test.alun.app/api/token", {
			username: "username",
			password: "password"
		})

		if(tokenRes.data.success) {
			token = tokenRes.data.result
			try {
				// const response = await axios.get(`https://sp-api-test.alun.app/api/download/${data}`, {
				// 	headers: {
				// 		Authorization: `Bearer ${token}`
				// 	}
				// })
				// console.log(response)
				// if (response.data.success) {
				// 	console.log(response.data)
				// 	return response.data
				// } else {
				// 	console.error(response.message)
				// 	return response.data
				// }	
				const response = await axios({
					url: `https://sp-api-test.alun.app/api/download/${data}`,
					method: 'GET',
					headers: { Authorization: `Bearer ${token}` },
					responseType: 'blob'
				}).then((response) => {
					console.log(response)
					const url = window.URL.createObjectURL(new Blob([response.data]));
					const link = document.createElement('a');
					link.href = url;
					// link.setAttribute('download', 'proof'); //or any other extension
					document.body.appendChild(link);
					link.click();
				})
			} catch (err) {
				console.error(err)
				return err
			}
		}
	} catch (err) {
		console.error(err)
		return err
	}
}
