import axios from "axios"

export default async function updateFaculty() {
	try {
		let token = null
		const tokenRes = await axios.post("https://sp-api-test.alun.app/api/token", {
			username: "username",
			password: "password"
		})

		if(tokenRes.data.success) {
			token = tokenRes.data.result
			try {
				const response = await axios.put("https://sp-api-test.alun.app/api/faculty/basic-info/9/personal", {
					lastName: "Cristobal",
					middleName: "Pacay",
					permanentAddress: "037 R. Higgins St., Brgy. 185, Pasay City",
					presentAddress: "037 R. Higgins St., Brgy. 185, Pasay City",
					mobile: "09123456789",
					landline: "83539084",
					email: "jodie@email.com",
					civilStatus: "single",
					religion: "Catholic",
					emergencyContactPerson: "Hilda Cristobal",
	  				emergencyContactNumber: "09123456789"
				}, {
					headers: {
						Authorization: `Bearer ${token}`
					}
				})
				if (response.data.success) {
					console.log(response)
					return response.data
				} else {
					console.error(response.message)
					return response.data
				}	
			} catch (err) {
				console.error(err)
				return err
			}
		}
	} catch (err) {
		console.error(err)
		return err
	}
}
