import axios from "axios"

export default async function addPublication(data) {
	try {
		let token = null
		const tokenRes = await axios.post("https://sp-api-test.alun.app/api/login", {
			upemail: "username",
			password: "password"
		})

		if(tokenRes.data.success) {
			token = tokenRes.data.result.token
			try {
				const response = await axios.post("https://sp-api-test.alun.app/api/faculty/accomplishment/add/publication", {
					title: `${data.title}`,
					journal: `${data.journal}`,
					url: `${data.url}`,
					publicationDate:`${data.publicationDate}`,
					nonFacultyAuthors: `${data.nonFacultyAuthors}`
				}, {
					headers: {
						Authorization: `Bearer ${token}`
					}
				})
				if (response.data.success) {
					// console.log(response.data)
					try {
						const pub = await axios.post("https://sp-api-test.alun.app/api/faculty/accomplishment/add/publisher", {
							facultyId: 9,
							publicationId: response.data.result.publicationId,
							status: "Pending"
						}, {
							headers: {
								Authorization: `Bearer ${token}`
							}
						})
						if (pub.data.success) {
							console.log(pub.data)
							return pub.data	
						} else {
							console.error(pub.message)
							return pub.data
						}
					} catch (err) {
						console.error(err)
						return err
					}	
				} else {
					console.error(response.message)
					return response.data
				}	
			} catch (err) {
				console.error(err)
				return err
			}
		}
	} catch (err) {
		console.error(err)
		return err
	}
}
