				let postData = new FormData();
				postData.append('facultyId', '9');
				postData.append('institutionSchool', `${data.institutionSchool}`);
				postData.append('degreeCert', `${data.degreeCert}`);
				postData.append('majorSpecialization', `${data.majorSpecialization}`);
				postData.append('startDate', `${data.startDate}`);
				postData.append('endDate', `${data.endDate}`);
				postData.append('proof', `${data.proof}`);
				const response = await axios({
				    method: 'post',
				    url: 'https://sp-api-test.alun.app/api/faculty/basic-info/add/education',
				    data: postData,
				    headers: {'Content-Type': 'multipart/form-data', Authorization: `Bearer ${token}` }
			    })	
			    .then(function (response) {
			        //handle success
			        console.log(response);
			    })
