import axios from "axios"

export default async function addEducation(data, formData) {
	try {
		let token = null
		const tokenRes = await axios.post("https://sp-api-test.alun.app/api/login", {
			upemail: "username",
			password: "password"
		})

		if(tokenRes.data.success) {
			token = tokenRes.data.result.token
			try {
				if(formData.get('endDate') == "") {
					formData.delete('endDate')
				}
				for (var value of formData.values()) {
 					  console.log(value);
				}
				const response = await axios({
				    method: 'POST',
				    url: 'https://sp-api-test.alun.app/api/faculty/basic-info/add/education',
				    data: formData,
				    headers: {'Content-Type': 'multipart/form-data', Authorization: `Bearer ${token}`}
			    })	
			    .then(function (response) {
			        //handle success
			        console.log(response);
			    })
			    .catch(function (response) {
			        //handle error
			        console.log(response);
			    });
			} catch (err) {
				console.error(err)
				return err
			}
		}
	} catch (err) {
		console.error(err)
		return err
	}
}
