import axios from "axios"

export default async function updateFaculty(data) {
	try {
		let token = null
		const tokenRes = await axios.post("https://sp-api-test.alun.app/api/token", {
			username: "username",
			password: "password"
		})

		if(tokenRes.data.success) {
			token = tokenRes.data.result
			// let bod = {
			// 	facultyId: "9",
			// 	institutionSchool: `${data.institutionSchool}`,
			// 	degreeCert: `${data.degreeCert}`,
			// 	majorSpecialization: `${data.majorSpecialization}`,
			// 	startDate: `${data.startDate}`,
			// 	endDate: `${data.endDate}`,
			// 	proof: `${data.proof}`,
			// 	status: "For Verification"
			// }
			// console.log(bod)
			try {
				const response = await axios.post("https://sp-api-test.alun.app/api/faculty/basic-info/add/education", {
					facultyId: "9",
					institutionSchool: "UPD",
					degreeCert: "Fine Arts",
					majorSpecialization: "Acting",
					startDate: "2019-09-13",
					endDate: "2020-05-18",
					proof: "proof.jpg",
					status: "For Verification"
				})
				console.log(response)
				if (response.data.success) {
					console.log("yey")
					console.log(response.data)
					return response.data
				} else {
					console.log("ugh")
					console.error(response.message)
					return response.data
				}	
			} catch (err) {
				console.error(err)
				return err
			}
		}
	} catch (err) {
		console.error(err)
		return err
	}
}
